tAnimateCfg; // 图表绘制完成，数据发生变更后，被销毁图形的销毁动画\n}\n\ninterface ComponentAnimateCfg {\n  animation?: string; // 动画效果，内置的动画效果见下表，也可以通过自定义动画的方式实现自定义效果\n  duration?: number; // 动画执行时间\n  easing?: string; // 动画缓动函数\n  delay?: number; // 动画延迟时间\n}\n",paraId:7,tocIndex:1},{value:"其中 ",paraId:8,tocIndex:1},{value:"animation",paraId:8,tocIndex:1},{value:" 传入动画函数名称，内置默认动画函数如下表，同时也可以通过 ",paraId:8,tocIndex:1},{value:"registerAnimation",paraId:8,tocIndex:1},{value:" 自定义动画函数。",paraId:8,tocIndex:1},{value:"动画效果",paraId:9,tocIndex:1},{value:"，前往",paraId:9,tocIndex:1},{value:"图表示例",paraId:10,tocIndex:1},{value:"查看效果",paraId:9,tocIndex:1},{value:"animation",paraId:11,tocIndex:1},{value:"效果",paraId:11,tocIndex:1},{value:"说明",paraId:11,tocIndex:1},{value:"不适用场景",paraId:11,tocIndex:1},{value:"'fade-in'",paraId:11,tocIndex:1},{value:"渐现动画。",paraId:11,tocIndex:1},{value:"'fade-out'",paraId:11,tocIndex:1},{value:"渐隐动画。",paraId:11,tocIndex:1},{value:"'grow-in-x'",paraId:11,tocIndex:1},{value:"容器沿着 x 方向放大的矩阵动画，多用于 G.Group 容器类进行动画。",paraId:11,tocIndex:1},{value:"不适用于饼图、玫瑰图等 polar、theta 坐标系下的图表以及柱、条状图",paraId:11,tocIndex:1},{value:"'grow-in-y'",paraId:11,tocIndex:1},{value:"容器沿着 y 方向放大的矩阵动画，多用于 G.Group 容器类进行动画。",paraId:11,tocIndex:1},{value:"不适用于饼图、玫瑰图等 polar、theta 坐标系下的图表以及柱、条状图",paraId:11,tocIndex:1},{value:"'grow-in-xy'",paraId:11,tocIndex:1},{value:"容器沿着 x,y 方向放大的矩阵动画，多用于 G.Group 容器类进行动画。",paraId:11,tocIndex:1},{value:"'scale-in-x'",paraId:11,tocIndex:1},{value:"单个图形沿着 x 方向的生长动画。",paraId:11,tocIndex:1},{value:"'scale-in-y'",paraId:11,tocIndex:1},{value:"单个图形沿着 y 方向的生长动画。",paraId:11,tocIndex:1},{value:"'wave-in'",paraId:11,tocIndex:1},{value:"划入入场动画效果，不同坐标系下效果不同。",paraId:11,tocIndex:1},{value:"'zoom-in'",paraId:11,tocIndex:1},{value:"沿着图形中心点的放大动画。",paraId:11,tocIndex:1},{value:"'zoom-out'",paraId:11,tocIndex:1},{value:"沿着图形中心点的缩小动画。",paraId:11,tocIndex:1},{value:"'path-in'",paraId:11,tocIndex:1},{value:"path 路径入场动画。",paraId:11,tocIndex:1},{value:"'position-update'",paraId:11,tocIndex:1},{value:"图形位置移动动画。",paraId:11,tocIndex:1},{value:"限用于图表标签 label 上",paraId:11,tocIndex:1},{value:"Easing Effects",paraId:5},{value:"easing",paraId:12,tocIndex:2},{value:" 用来控制动画中的缓动效果，更多的缓动效果可以参见 ",paraId:12,tocIndex:2},{value:"d3-ease",paraId:12,tocIndex:2},{value:"。",paraId:12,tocIndex:2}]},22536:function(a,e,d){d.r(e),d.d(e,{texts:function(){return I}});const I=[{value:"在 G2Plot 中(继承 G2 事件)，我们将事件分成为几种事件类型：",paraId:0,tocIndex:0},{value:"主要包含有 DOM 的基础事件。",paraId:1,tocIndex:1},{value:"mouse 事件",paraId:2,tocIndex:1},{value:"mousedown",paraId:3,tocIndex:1},{value:"mousemove",paraId:3,tocIndex:1},{value:"mouseup",paraId:3,tocIndex:1},{value:"mouseover",paraId:3,tocIndex:1},{value:"mouseout",paraId:3,tocIndex:1},{value:"mouseenter",paraId:3,tocIndex:1},{value:"mouseleave",paraId:3,tocIndex:1},{value:"touch 事件 (移动端事件)",paraId:2,tocIndex:1},{value:"touchstart",paraId:4,tocIndex:1},{value:"touchmove",paraId:4,tocIndex:1},{value:"touchend",paraId:4,tocIndex:1},{value:"touchcancel",paraId:4,tocIndex:1},{value:"drag 事件",paraId:2,tocIndex:1},{value:"dragenter",paraId:5,tocIndex:1},{value:"dragover",paraId:5,tocIndex:1},{value:"dragleave",paraId:5,tocIndex:1},{value:"drop",paraId:5,tocIndex:1},{value:"contextmenu 右键事件",paraId:2,tocIndex:1},{value:"dblclick 双击事件",paraId:2,tocIndex:1},{value:"基础事件",paraId:6,tocIndex:2},{value:"中，只要画布中触发这些事件，都会执行，但是大部分场景下，我们需要精确定位到某一个元素的点击，比如：",paraId:6,tocIndex:2},{value:"柱形图的柱子被 click 的时候",paraId:7,tocIndex:2},{value:"图例的某一项被 hover 的时候",paraId:7,tocIndex:2},{value:"坐标轴的标签被 dblclick 的时候",paraId:7,tocIndex:2},{value:"...",paraId:7,tocIndex:2},{value:"这种情况我们就可以使用 G2 的组合事件，G2 的组合事件规则为：",paraId:8,tocIndex:2},{value:"组件名:基础事件名",paraId:8,tocIndex:2},{value:"，即：",paraId:8,tocIndex:2},{value:"${componentName}:${eventName}\n",paraId:9,tocIndex:2},{value:"例如对应上述的几个场景，事件名称为：",paraId:10,tocIndex:2},{value:"element:click",paraId:11,tocIndex:2},{value:"legend-item:mouseover",paraId:11,tocIndex:2},{value:"axis-label:dblclick",paraId:11,tocIndex:2},{value:"...",paraId:11,tocIndex:2},{value:"G2Plot(G2) 内置的组件中，componentName 的分类很细，可以以下面的一个图进行大概说明。",paraId:12,tocIndex:2},{value:"也就是大致可以分成为：",paraId:13,tocIndex:2},{value:"plot",paraId:14,tocIndex:2},{value:"axis\n",paraId:14,tocIndex:2},{value:"axis-line",paraId:15,tocIndex:2},{value:"axis-label",paraId:15,tocIndex:2},{value:"legend\n",paraId:14,tocIndex:2},{value:"legend-item",paraId:16,tocIndex:2},{value:"label",paraId:14,tocIndex:2},{value:"slider",paraId:14,tocIndex:2},{value:"element\n",paraId:14,tocIndex:2},{value:"interval",paraId:17,tocIndex:2},{value:"line",paraId:17,tocIndex:2},{value:"area",paraId:17,tocIndex:2},{value:"point",paraId:17,tocIndex:2},{value:"polygon",paraId:17,tocIndex:2},{value:"schema",paraId:17,tocIndex:2},{value:"path",paraId:17,tocIndex:2},{value:"...",paraId:17,tocIndex:2},{value:"然后将这些组件名称和基础事件名进行一个排列组合，即为 G2Plot(G2) 内置的事件。",paraId:18,tocIndex:2},{value:"在 Plot 上通过 ",paraId:19,tocIndex:3},{value:"on",paraId:19,tocIndex:3},{value:" 绑定事件、",paraId:19,tocIndex:3},{value:"off",paraId:19,tocIndex:3},{value:" 移除绑定事件。",paraId:19,tocIndex:3},{value:"// 绑定事件\nplot.on('eventName', callback);\n// 绑定事件，只触发一次\nplot.once('eventName', callback);\n// 移除事件\nplot.off('eventName', callback);\n",paraId:20,tocIndex:3},{value:"组合方式: ",paraId:21,tocIndex:3},{value:"${componentName}:${eventName}",paraId:21,tocIndex:3},{value:"// plot 添加点击事件,整个图表区域\nplot.on('plot:click', (...args) => {\n  console.log(...args);\n});\n\n// element 添加点击事件， element 代表图形元素，关于图形元素，请查看：https://g2.antv.vision/zh/docs/manual/concepts/element\nplot.on('element:click', (...args) => {\n  console.log(...args);\n});\n\n// 图例添加点击事件\nplot.on('legend-item:click', (...args) => {\n  console.log(...args);\n});\n\n// 图例名称添加点击事件\nplot.on('legend-item-name:click', (...args) => {\n  console.log(...args);\n});\n\n// 给 tooltip 添加点击事件\nplot.on('tooltip:show', (...args) => {\n  console.log(...args);\n});\n\nplot.on('tooltip:hide', (...args) => {\n  console.log(...args);\n});\n\nplot.on('tooltip:change', (...args) => {\n  console.log(...args);\n});\n\n// label 添加点击事件\nplot.on('label:click', (...args) => {\n  console.log(...args);\n});\n\n// mask 添加点击事件\nplot.on('mask:click', (...args) => {\n  console.log(...args);\n});\n\n// axis-label 添加点击事件\nplot.on('axis-label:click', (...args) => {\n  console.log(...args);\n});\n\n// 给 annotation 添加点击事件\nplot.on('annotation:click', (...args) => {\n  console.log(...args);\n});\n",paraId:22,tocIndex:3}]},94357:function(a,e,d){d.r(e),d.d(e,{texts:function(){return I}});const I=[{value:"全局 G2Plot 对象，在 script 标签引入 g2plot.min.js 文件后获得，或者通过 ",paraId:0},{value:"import",paraId:0},{value:" 或 ",paraId:0},{value:"require",paraId:0},{value:" 进行引用。",paraId:0},{value:" ",paraId:1,tocIndex:1},{value:"Function",paraId:1,tocIndex:1},{value:" ",paraId:1,tocIndex:1},{value:"setGlobal(globals: Object): void;\n",paraId:2,tocIndex:1},{value:"设置全局变量。",paraId:3,tocIndex:1},{value:"目前支持的全局变量有：",paraId:4,tocIndex:1},{value:"locale",paraId:5,tocIndex:1},{value:" 使用的语言，内置 'zh-CN' 和 'en-US'。",paraId:5,tocIndex:1},{value:"使用示例：",paraId:6,tocIndex:1},{value:"// 方式1: 浏览器\n<script type=\"text/javascript\" src=\"https://unpkg.com/@antv/g2plot@latest/dist/g2plot.min.js\"><\/script>\n<script>\n  const { setGlobal } = G2Plot;\n<\/script>\n\n// 方式2\nimport { setGlobal } from '@antv/g2plot';\n\n// 设置全局语言\nsetGlobal({ locale: 'zh-CN' });\n",paraId:7,tocIndex:1},{value:" ",paraId:8,tocIndex:2},{value:"Function",paraId:8,tocIndex:2},{value:" ",paraId:8,tocIndex:2},{value:"注册语言包。",paraId:9,tocIndex:2},{value:"registerLocale(locale: string, localeCfg: Object): void;\n",paraId:10,tocIndex:2},{value:"目前内置 'zh-CN' and 'en-US' 两个语言，你也可以使用 ",paraId:11,tocIndex:2},{value:"G2Plot.registerLocale",paraId:11,tocIndex:2},{value:" 方法注册新的语言。语言包格式参考：",paraId:11,tocIndex:2},{value:"src/locales/en_US.ts",paraId:11,tocIndex:2},{value:"获取 G2 对象，相当于引入了 G2 的全部能力。借助 G2 对象，我们可以自定义交互、自定义 Shape、自定义主题等。",paraId:12,tocIndex:3},{value:"使用示例：",paraId:13,tocIndex:3},{value:"使用 G2 的自定义注册主题机制，进行主题定制。",paraId:14,tocIndex:3}]},74751:function(a,e,d){d.r(e),d.d(e,{texts:function(){return I}});const I=[{value:"交互（Interaction）是 G2 中的重要 API，通过这个方法可以加载 G2 内置的交互，或者基于交互语法形式自定义的 Interaction 交互。G2 4.0 在交互方面做了非常大的调整，所有的交互代码都是插入式的，通过交互语法进行组织。使用交互的方式也非常简单，仅需要设置交互的名称即可。",paraId:0},{value:"在 G2Plot 中，透传了 G2 的交互语法，同时也内置了一些与具体 plot 绑定的交互。",paraId:1},{value:"// 开启「鼠标移入图表元素（柱状图的柱子、点图的点等）时触发 active」的交互\ninteractions: [{ type: 'element-active' }]\n\n// 开启多个交互\ninteractions: [{ type: 'element-active' }, { type: 'brush' }]\n",paraId:2,tocIndex:1},{value:"通过 ",paraId:3,tocIndex:2},{value:"cfg",paraId:3,tocIndex:2},{value:" 可以对交互行为进行配置，详细参考 ",paraId:3,tocIndex:2},{value:"G2 | 修改交互的默认交互",paraId:3,tocIndex:2},{value:"// 修改 tooltip 触发事件\ninteractions: [\n  { \n    type: 'tooltip',\n    cfg: { start: [{ trigger: 'element:click', action: 'tooltip:show' }] } \n  }\n]\n",paraId:4,tocIndex:2},{value:"// 方式1: 关闭 tooltip 交互\ninteractions: [{ type: 'tooltip', enable: false }]\n\n// 方式2:\nplot.chart.removeInteraction('interaction-type');\n",paraId:5,tocIndex:3},{value:"使用示例：",paraId:6,tocIndex:3},{value:"// 移除 图例筛选 交互\nplot.chart.removeInteraction('legend-filter');\n",paraId:7,tocIndex:3},{value:"更多内置交互列表，见",paraId:8,tocIndex:5},{value:"G2 | 交互",paraId:8,tocIndex:5},{value:"交互名称",paraId:9,tocIndex:5},{value:"预览",paraId:9,tocIndex:5},{value:"备注",paraId:9,tocIndex:5},{value:"element-active ",paraId:9,tocIndex:5},{value:"element-selected ",paraId:9,tocIndex:5},{value:"可以同时选中多个 element",paraId:9,tocIndex:5},{value:"element-single-selected  ",paraId:9,tocIndex:5},{value:"element-highlight ",paraId:9,tocIndex:5},{value:"element-highlight-by-color ",paraId:9,tocIndex:5},{value:"element-highlight-by-x ",paraId:9,tocIndex:5},{value:"相同横轴字段进行同时高亮。对于柱状图(column) ，横轴字段是 xField，对于条形图(bar) ，横轴字段是 yField。",paraId:9,tocIndex:5},{value:"legend-highlight",paraId:9,tocIndex:5},{value:"图例项高亮，对应的图表元素也高亮",paraId:9,tocIndex:5},{value:"axis-label-highlight",paraId:9,tocIndex:5},{value:"action 的详细说明，见 ",paraId:10,tocIndex:6},{value:"G2 | 交互反馈",paraId:10,tocIndex:6},{value:"Action 名称",paraId:11,tocIndex:6},{value:"说明",paraId:11,tocIndex:6},{value:"作用于",paraId:11,tocIndex:6},{value:"element-link-by-color",paraId:11,tocIndex:6},{value:"用于连接相同颜色的图表元素，一般用于层叠柱状图",paraId:11,tocIndex:6},{value:"Element",paraId:11,tocIndex:6},{value:"element-highlight",paraId:11,tocIndex:6},{value:"用于设置和取消图表元素的 highlight，支持多个元素一起 highlight ",paraId:11,tocIndex:6},{value:"Element",paraId:11,tocIndex:6},{value:"element-single-highlight",paraId:11,tocIndex:6},{value:"用于设置和取消图表元素的 highlight ，只允许单个元素 highlight。高亮的时候会取消当前激活元素之外的元素的高亮态",paraId:11,tocIndex:6},{value:"Element",paraId:11,tocIndex:6},{value:"element-filter",paraId:11,tocIndex:6},{value:"图表元素的过滤，支持来自图例（分类和连续）、坐标轴文本的触发",paraId:11,tocIndex:6},{value:"Element",paraId:11,tocIndex:6},{value:"使用方式:",paraId:12,tocIndex:8},{value:"G2.registerInteraction('element-link', {\n  start: [\n    {trigger: 'interval:mouseenter', action: 'element-link-by-color:link'}\n  ],\n  end: [\n    {trigger: 'interval:mouseleave', action: 'element-link-by-color:unlink'}\n  ]\n});\n\n// options\n{\n  // interactions: [{ type: 'element-link' }],\n  // 搭配高亮\n  interactions: [{ type: 'element-link' }, { type: 'element-highlight-by-color' }],\n}\n",paraId:13,tocIndex:8},{value:"使用方式：",paraId:14,tocIndex:9},{value:"G2.registerInteraction('hover-cursor', {\n  showEnable: [\n    { trigger: 'element:mouseenter', action: 'cursor:pointer' },\n    { trigger: 'element:mouseleave', action: 'cursor:default' },\n  ],\n});\n\n// options\n{\n  // 搭配 元素高亮\n  interactions: [{ type: 'element-highlight' }, { type: 'hover-cursor' }],\n}\n",paraId:15,tocIndex:9},{value:"更多关于交互的使用说明，见 ",paraId:16,tocIndex:10},{value:"G2 文档",paraId:16,tocIndex:10}]},85111:function(a,e,d){d.r(e),d.d(e,{texts:function(){return I}});const I=[{value:"通过 ",paraId:0},{value:"meta",paraId:0},{value:" 可以全局化配置图表数据元信息，以字段为单位进行配置。在 ",paraId:0},{value:"meta",paraId:0},{value:" 上的配置将同时影响所有组件的文本信息。",paraId:0},{value:"传入以字段名为 key，",paraId:1,tocIndex:0},{value:"Meta